package msmk.kindergarten.com.greendaotest;import android.app.Application;import com.facebook.stetho.Stetho;/** * Created by a5yearge on 2018/5/24. */public class GreenDaoApp extends Application{    AppComponent appComponent;    @Override    public void onCreate() {        super.onCreate();        injector();        Stetho.initializeWithDefaults(this);    }    private void injector(){        appComponent = DaggerAppComponent.builder().greenDaoModule(new GreenDaoModule(this)).build();    }    public AppComponent getAppComponent(){        return  appComponent;    }}