package msmk.kindergarten.com.greendaotest;import android.database.sqlite.SQLiteDatabase;import javax.inject.Singleton;import dagger.Module;import dagger.Provides;/** * Created by a5yearge on 2018/5/24. */@Modulepublic class GreenDaoModule {    private final GreenDaoApp application;    public GreenDaoModule(GreenDaoApp application){        this.application = application;    }    @Provides    @Singleton    public GreenDaoApp provideApplication(){        return application;    }    @Provides    @Singleton    SQLiteDatabase provideSQLiteDatabase(GreenDaoApp baseApp){        DaoMaster.OpenHelper helper = new DaoMaster.DevOpenHelper(baseApp, "green_dao_db", null);        SQLiteDatabase sqlDB = helper.getWritableDatabase();        return sqlDB;    }    @Provides    @Singleton    DaoMaster provideDaoMaster(SQLiteDatabase sqLiteDatabase){        DaoMaster daoMaster = new DaoMaster(sqLiteDatabase);        return daoMaster;    }    @Provides    @Singleton    DaoSession provideDaoSession(DaoMaster daoMaster){        DaoSession daoSession = daoMaster.newSession();        return daoSession;    }}